import  Filter from "./userservices/userservice";
import emojiList from ""
export default function FilterEmoji(searchText,maxResults) {
   const emojiList=Filter();
  return (emojiList
    .filter(emoji => {
      if (emoji.title.toLowerCase().includes(searchText.toLowerCase())) {
        return true;
      }
      if (emoji.keywords.includes(searchText)) {
        return true;
      }
      return false;
    })
    .slice(0, maxResults))
}
